function connect(event) {
    username = document.querySelector('#name').value.trim();

    if(username) {
        usernamePage.classList.add('hidden');
        chatPage.classList.remove('hidden');

        var socket = new SockJS('/ws');
        stompClient = Stomp.over(socket);

        stompClient.connect({}, onConnected, onError);
        stompClient.debug = null
    }
    event.preventDefault();
}


function onConnected() {
    // Subscribe to the Public Topic
//    stompClient.subscribe('/topic/messages/'+username, onMessageReceived);

    stompClient.subscribe( "/user/" + '113' + "/queue/messages", onMessageReceived);
	console.log('vcc ' + "/user/" + 'xuandien369' + "/queue/messages")
    stompClient.subscribe('/app/public', onMessageReceived);
    // Tell your username to the server
    stompClient.send("/app/chat.addUser",
        {},
        JSON.stringify({sender: username, type: 'JOIN'})
    )

    connectingElement.classList.add('hidden');
}


function onError(error) {
    connectingElement.textContent = 'Could not connect to WebSocket server. Please refresh this page to try again!';
    connectingElement.style.color = 'red';
}

