const getBase64 = file =>{
        var reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = function () {
            fileEndcode = reader.result;
			console.log('lan 2',typeof fileEndcode);
			console.log('imageURL length',imageURL.length);
			console.log('fileEndcode length',fileEndcode.length);
        };
        reader.onerror = function (error) {
            console.log('Error: ', error);
        };
}

function send(event) {
    var messageContent = messageInput.value.trim();
    if( stompClient) {
		if(messageContent && !fileEndcode){
			var chatMessage = {
				sender: username,
				content: messageInput.value,
				type: 'CHAT'
			};
			stompClient.send("/app/xd369", {}, JSON.stringify(chatMessage));
			messageInput.value = '';
		}
		else {
			var chatMessage = {
				imageURL:fileEndcode,
				sender: username,
				type: 'CHAT'
			};
			stompClient.send("/app/xd369", {}, JSON.stringify(chatMessage));
			messageInput.value = '';
			fileEndcode=undefined;
			fileUpload.value ='';
		}
    }
    event.preventDefault();
}


